namespace EvaluacionPractica_partedos{    internal class Program    {        static void Main(string[] args)        {            double monto_compra = 0;            int dia = 0;            string dia_letra;            int categoria = 0;            string categoria_letra = "";            double descuento = 0.00;            string nombre = "";            string direccion = "";            string telefono = "";            double total_pagar = 0.00;            string nombre_vendedor = "";            double desc_valor = 0.00;            int bandera = 0;            do
            {                Console.WriteLine("\n-----------------Datos ---------------------");                Console.Write("Ingrese nombre del cliente: ");                nombre = Console.ReadLine();                Console.Write("Ingrese direccion del cliente: ");                direccion = Console.ReadLine();                Console.Write("Ingrese numero de telefono del cliente: ");                telefono = Console.ReadLine();                Console.Write("Ingrese nombre del vendedor: ");                nombre_vendedor = Console.ReadLine();                Console.WriteLine("\n--------------Menu----------------");                Console.WriteLine("Ingrese el numero correspondiente a la categoria");                Console.WriteLine("1 - niños");                Console.WriteLine("2 - mujer");                Console.WriteLine("3 - hombre");                Console.Write("Respuesta: ");                categoria = int.Parse(Console.ReadLine());                Console.Write("\nIngrese el monto de su compra: ");                monto_compra = double.Parse(Console.ReadLine());                Console.WriteLine("\nIngrese el numero correspondiente al dia ");                Console.WriteLine("1 - Lunes");                Console.WriteLine("2 - Martes");                Console.WriteLine("3 - Miercoles");                Console.WriteLine("4 - Jueves");                Console.WriteLine("5 - Viernes");                Console.WriteLine("6 - Sabado");                Console.WriteLine("7 - Domingo");                Console.Write("Respuesta: ");                dia = int.Parse(Console.ReadLine());




















                /*compra>= 150 && dia==”viernes” && categoría ==ninios then descuento ==5% else descuento =2.5%                compra>199.99 && dia==”domingo” && categoría ==mujer then descuentto=10% else 5%                Categoría==hombre && dia==”sábado” && compra>=250 then descuento 15% else 7.5%                Else                 Print monto;                Print mensaje=” no aplica a ningún descuento”                */
                switch (categoria)                {                    case 1:                        categoria_letra="Niños";                        if (monto_compra>=150)                        {                            if (dia==5)                            {                                descuento = monto_compra * 0.05;                                total_pagar = monto_compra - descuento;                            }                            else                            {                                descuento = monto_compra * 0.025;                                total_pagar = monto_compra - descuento;                            }                        }                        else                        {                            descuento = 0;                            total_pagar = 0;                        }                        break;                    case 2:                        categoria_letra="Mujer";                        if (monto_compra>199.99)                        {                            if (dia==7)                            {                                descuento = monto_compra * 0.10;                                total_pagar = monto_compra - descuento;                            }                            else                            {                                descuento = monto_compra * 1.05;                                total_pagar = monto_compra - descuento;                            }                        }                        else                        {                            descuento = 0;                            total_pagar = 0;                        }                        break;                    case 3:                        categoria_letra="Hombre";                        if (monto_compra>=250)                        {                            if (dia==6)                            {                                descuento = monto_compra * 0.15;                                total_pagar = monto_compra - descuento;                            }                            else                            {                                descuento = monto_compra * 0.075;                                total_pagar = monto_compra - descuento;                            }                        }                        else                        {                            descuento = 0;                            total_pagar = 0;                        }                        break;                    default:                        categoria_letra="N/A";                        desc_valor = 0;                        descuento = monto_compra * desc_valor;                        total_pagar = monto_compra - descuento;                        break;                }                switch (dia)                {                    case 1:                        dia_letra="Lunes";                        break;                    case 2:                        dia_letra="Martes";                        break;                    case 3:                        dia_letra="Miercoles";                        break;                    case 4:                        dia_letra="Jueves";                        break;                    case 5:                        dia_letra="Viernes";                        break;                    case 6:                        dia_letra="Sabado";                        break;                    case 7:                        dia_letra="Domingo";                        break;                    default:                        dia_letra="N/A";                        break;                }
